import React, { useEffect, useState } from 'react'
import Nav1 from "./Nav"
import Footer from "./Footer"
import {Card,Button,Row,Container,Col} from 'react-bootstrap'
import { useParams} from 'react-router-dom'
import { productDetails,getColor } from '../config/Proservice'
import ReactImageMagnify from "react-image-magnify"
import { width } from '@mui/system'

export default function Productdetails() {
    const {id}= useParams();
    const[details,setDetails]= useState([]);
    const[col,setCol]=useState('');
    const [images,setimages]=useState([]);
    const [mImage,setMimage]=useState('');

    useEffect(()=>{
        productDetails(id).then((res)=>{
            setDetails([res.data]);
            setimages(res.data.subimages)
            setMimage(res.data.product_image)
            getColor(res.data.color_id).then((res)=>{
                setCol(res.data.color_code)
            })
        })
    },[])
    return (
        <div>
           <Nav1/>
            <br></br>
            <div>
            <Container>
            {details.map((item,index)=>{
                return(
        <Row>
        <Col md={6}>
        <ReactImageMagnify
        {...{
            smallImage:{
                alt:"george",
                isFluidWidth:true,
                src:mImage
            },
            largeImage:{
                src:mImage,
                width:1000,
                height:1000,
            }
        }}
        />
       
        {images.map((value,index)=>{
            return(
                <img src={value} style={{width: "8rem"}} height="100px" onClick={()=>setMimage(value)} />
            )


        })} 
        
       </Col>
       
        
        <Col md={6}>
            <h2>Product Detail</h2>
                
                
                    
                        <Card style={{ width: "30rem", marginLeft:"5rem" }}>
                           
                            <Card.Body>
                                <h3> {product_name} </h3>
                                <h5><b>Price: {product_cost}</b>&nbsp; &nbsp; </h5>
                                <h5><b>Rating:</b>&nbsp; &nbsp; </h5>
                                <h5><b>Share</b>&nbsp; &nbsp; </h5>

                                 <Button variant="primary" >Add to cart</Button>
                           
                            </Card.Body>
                        </Card> 
                  
                
            
                </Col>
                </Row>
            )})}
        
            </Container>
            </div>
            
            <br></br>
            <Footer/>
        </div>
    )
}
