import React, { useEffect } from 'react'
import { Button, Card, Col, Container, Row } from 'react-bootstrap'
import Footer from './Footer'
import Nav1 from './Nav'
import { useState } from 'react'
import { getOrderDetails } from '../config/Proservice'

export default function Order() {
    let email;
    let token=localStorage.getItem('_token')
    if(token){
        let decode = jwt_decode("_token")
        email=decode.oid
    }

    const [details,setdetails]=useState([]);
    const [oimage,setOimage]=useStatate([]);

    useEffect(()=>{
        getOrderDetails(email).then(res=>{
            setdetails(res.data)
            let ele = res.data.forEach(element=>{
                setOimage(element.cart)
            })
        })
    },[])
    return (
        <div>
        <Nav1/>
         <br></br>

        <div>
        
    
            <Container>
            <Card style={{width:"40rem" , marginLeft:"5rem"}}>
            <Row>
                <Col md={6} ml={5}>
              
                    <h5>Date</h5>
                    <h5>Status</h5>
                   

                </Col>
                <Col md={6}>
                    <h5>Order id</h5>
                    <h5>Order total</h5>
                </Col>
            </Row>
            </Card>
            </Container>
            <br></br>
            <Container>
            <Card style={{width:"40rem", marginLeft:"5rem"}}>
                <img style={{width:"20rem"}} height="200px" />
                <br></br>
                <Button variant='secondary'>Download Invoice as PDF</Button>
            </Card>    
            </Container>
          
            <br></br>
        </div>
        <Footer/>
        </div>
    )
}
