import React from 'react';
import { Button, Container, Table, Form, Row, Col,Card } from "react-bootstrap";
import Nav1 from "../component/Nav"
import Footer from "../component/Footer"
import  { useEffect,  useState } from "react";
import { useNavigate } from "react-router";


export default function Cart() {
    let items=[];
    const navigate = useNavigate();
    const [cart, setCart] = useState([]);
    
    let total = [0];
    const [quantity, setQuantity] = useState("");
    useEffect(() => {
        let cartItems = JSON.parse(localStorage.getItem("mycart"));
        setCart(cartItems);
    }, []);
    console.log(cart);

    const onChangeHandler = (event) => {
        setQuantity(event.target.value);
        console.log(event.target.value);
    };

    const onAdd = (product) => {
        const exist = cart.find((item) => item._id === product._id);
        if (exist) {
            setCart(
                cart.map((item) =>
                    item._id === product._id
                        ? { ...exist, quantity: exist.quantity + 1 }
                        : item
                )
            );
            localStorage.setItem("mycart", JSON.stringify(cart));
        } else {
            setCart([...cart, { ...product, quantity: 1 }]);
        }
    };

    const onRemove = (product) => {
        const exist = cart.find((item) => item._id === product._id);
        if (exist.quantity === 1) {
            // setCart(cart.filter((item) => item._id !== product._id));
        } else {
            setCart(
                cart.map((item) =>
                    item._id === product._id
                        ? { ...exist, quantity: exist.quantity - 1 }
                        : item
                )
            );
            localStorage.setItem("mycart", JSON.stringify(cart));
        }
    };

    const onDelete = (index) => {
        let lstore = JSON.parse(localStorage.getItem("mycart"));
        lstore.splice(index, 1);
        console.log(lstore);
        let setStore = JSON.stringify(lstore);
        localStorage.setItem("mycart", setStore);
        setCart(lstore);
    };
    return (
        <div>
        <Nav1/>
        <br></br>
        <div>
        <Container style={{ backgroundColor: "lightgray" }}>
                <Table striped bordered hover size="sm">
                    <thead>
                        <tr>
                            <th>Sr.No</th>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Total</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {cart
                            ? cart.map((value, index) => {
                                  return (
                                      <tr key={index}>
                                          <td>{index + 1}</td>
                                          <td>{value.name}</td>
                                          <td>{value.price}/-</td>
                                          <td>
                                              <Row>
                                                  <Col>
                                                      <Button
                                                          variant="dark"
                                                          onClick={() =>
                                                              onRemove(value)
                                                          }
                                                      >
                                                          -
                                                      </Button>
                                                  </Col>
                                                  <Col>
                                                      <Form.Control
                                                          type="number"
                                                          placeholder="Enter quantity"
                                                          min="1"
                                                          max="20"
                                                          value={value.quantity}
                                                      />
                                                  </Col>
                                                  <Col>
                                                      <Button
                                                          variant="dark"
                                                          onClick={() =>
                                                              onAdd(value)
                                                          }
                                                      >
                                                          +
                                                      </Button>
                                                  </Col>
                                              </Row>
                                          </td>
                                          <td>
                                              {value.quantity * value.price}
                                          </td>
                                          <td>
                                              <Button
                                                  variant="danger"
                                                  onClick={() =>
                                                      onDelete(index)
                                                  }
                                              >
                                                  Delete
                                              </Button>
                                          </td>
                                          {console.log(
                                              total.push(
                                                  value.price * value.quantity
                                              )
                                          )}
                                      </tr>
                                  );
                              })
                            : ""}
                    </tbody>
                </Table>
               
                
            </Container>
            <Container>
            <Card style={{width:"20rem"}}>
            <h6 className="text-right">
                    Total Amount:{" "}
                    {total.reduce((result, number) => result + number)}
                </h6>
                <h6 className="text-right">
                    GST 5%:{" "}
                   {/*  {total.reduce((result, number) => result + number)} */}
                </h6>
                <Button variant='dark'>Process to buy</Button>
                </Card>
            </Container>
        </div>
        <br></br>
        <Footer/>
        </div>
    )
}
