import React, { useEffect } from 'react'
import { Button, Card, Col, Container, Row } from 'react-bootstrap'
import Footer from './Footer'
import Nav1 from './Nav'
import { useState } from 'react'
import { getOrderDetails } from '../config/Proservice'
import jwt_decode from 'jwt-decode'

export default function Order() {
    let email;
    let token=localStorage.getItem('_token')
    if(token){
        let decode = jwt_decode("_token")
        email=decode.oid
    } 

    const [details,setdetails]=useState([]);
    const [oimage,setOimage]=useState([]);

    useEffect(()=>{
        getOrderDetails(email).then(res=>{
            setdetails(res.data)
            let ele = res.data.forEach(element=>{
                setOimage(element.cart)
            })
        })
    },[])

    return (
        <div>
        <Nav1/>
         <br></br>

        <div>
            <Container className='m-4 ,p-4'>
            {details ? details.map((value,index)=>{
                return(
                    <Card>
                        <Card.header>
                            <Row>
                                <Col>
                                    <Card.Text as="h4"> Order Placed :</Card.Text>
                                    <Card.Text as ="h6"><span style={{color:"black"}}>{value.createdAt.substring(0,10)}</span></Card.Text>
                                </Col>
                                <Col style={{textAlign:"right"}}> 
                                    <Card.Text as="h4"> order ID:</Card.Text>
                                    <Card.text as="h6"><span style={{color:"black"}}>{value.id}</span></Card.text>
                                </Col>
                            </Row>
                            <Row>
                                <Col>
                                    <Card.Text as="h5">Status:<span style={{color:"blue"}}></span></Card.Text>
                                </Col>
                                <Col>
                                    <Card.Text as="h5" style={{textAlign:"right"}}>Total:<span style={{color:"blue"}}></span></Card.Text>
                                </Col>
                            </Row>
                        </Card.header>
                        <Card.body>
                            <Row>
                                <Card.Title>
                                {oimage.map((value,index)=>{
                                    return(
                                    
                                    <img src={value.image} alt="order_image" key={index} style={{height:"120px", width:"120px"}} 
                                        className='m-4'
                                    />
                                    )
                                })}

                                </Card.Title>
                            </Row>
                            <Button variant='dark'>Download Invoice As PDF</Button>
                        </Card.body>
                    </Card>
                )
            }):""}

            </Container>
        <br></br>
        </div>
        <Footer/>
        </div>
    )
}
